const headerMenuBtn=document.querySelector(".header__menu"),headerNav=document.querySelector(".header__nav");headerMenuBtn.addEventListener("click",()=>{headerMenuBtn.classList.contains("header__menu_active")?(headerMenuBtn.classList.remove("header__menu_active"),headerNav.classList.remove("header__nav_active")):(headerMenuBtn.classList.add("header__menu_active"),headerNav.classList.add("header__nav_active"))});