const orderForm=document.querySelector("form.order-form"),orderValue=document.querySelector('form.order-form input[type="number"]'),orderTotal=document.querySelector("form.order-form .order-form__total strong"),price=Number(document.querySelector("form.order-form [data-price]").innerHTML.slice(0,-1)),calculateTotalPrice=()=>{orderTotal.innerHTML=(price*orderValue.value).toLocaleString("en-US",{style:"currency",currency:"USD"})};orderValue.addEventListener("input",e=>Number(e.target.value)<Number(orderValue.min)?e.target.value=Number(orderValue.min):Number(e.target.value)>Number(orderValue.max)?e.target.value=Number(orderValue.max):void calculateTotalPrice()),window.addEventListener("load",calculateTotalPrice());